import React, { useEffect, useState } from 'react'
import axios from 'axios';


//const API_KEY = process.env.GIF;
const API_KEY = 'kJ42ythOe3B4PXTj3fFM000gXny6Aiz3';

const Random = () => {

        const[gif,setGif] = useState('');
        const[loading, setLoading] = useState(false);
       
   async  function fetchData(){
        const url =   `https://api.giphy.com/v1/gifs/random?api_key=${API_KEY}`;
        const {data} = await axios.get(url);
        const imageSource = data.data.images.downsized_large.url;
        console.log(imageSource);
        setGif(imageSource);
     }
     
     

     useEffect(()=>{
        fetchData();
     },[]);

        function clickHandler(){
            fetchData();
        }

  return (
    <div className='w-1/2  bg-green-500 rounded-lg items-center justify-evenly flex flex-col border border-black  '>
       
        <h1 className="text-xl font-bold mt-[15px]">
        <u>A RANDOM GIF</u></h1>

        <img src={gif} width='450' height={200}/>

        <button onClick={clickHandler}
         className="w-10/12 bg-white font-semibold opacity-90 text-lg py-2 rounded-lg mb-[20px]">
         GENERATE</button>

    </div>
  )
} 

export default Random
